mapper.select:
select rnum, program_no, program_title, act_bgn_date, act_end_date, recruit_stts, act_bgn_time, act_end_time, recruit_bgn_date, recruit_end_date, program_ctg, recruit_name, act_place, region_no from ( select rownum as rnum, program_no, program_title, act_bgn_date, act_end_date, recruit_stts, act_bgn_time, act_end_time, recruit_bgn_date, recruit_end_date, program_ctg, recruit_name, act_place, region_no from ( select program_no, program_title, act_bgn_date, act_end_date, act_bgn_time, act_end_time, recruit_stts, recruit_bgn_date, recruit_end_date, program_ctg, recruit_name, act_place, region_no from volunteer_program where enabled = 1
and (act_bgn_date <= #{searchIndex.endDate}) and (act_end_date) >= #{searchIndex.startDate})
and region_no = #{searchIndex.regionNo}
and program_ctg = #{searchIndex.ctgNo}
and adult_posbl = 1
and teen_posbl = 1
and program_title like '%' || #{searchIndex.keyword} || '%'
and recruit_stts = 1
and recruit_stts = 2
order by program_no desc ) where rownum <= #{pager.endRowNo} ) where rnum >= #{pager.startRowNo}

mapper.update:
update volunteer_program set 
	program_title = #{programTitle},
	act_bgn_date = #{actBgnDate},
 	act_end_date = #{actEndDate}, 
 	act_bgn_time = #{actBgnTime},
 	act_end_time = #{actEndTime}, 
 	recruit_bgn_date = #{recruitBgnDate},
 	recruit_end_date = #{recruitEndDate}, 
 	recruit_cnt = #{recruitCnt}, 
 	program_ctg = #{programCtg}, 
 	adult_posbl = #{adultPosbl},
 	teen_posbl = #{teenPosbl}, 
 	recruit_name = #{recruitName}, 
 	act_place = #{actPlace},
 	mng_name = #{mngName}, mng_tel = #{mngTel}, 
 	region_no = #{regionNo}, location = #{location},
 	content = #{content}

	, battach_oname=#{battachOname}
	,
	battach_type=#{battachType}
	, battach_data=#{battachData}


	, img_oname=#{imgOname}
	, img_type=#{imgType}
	,
	img_data=#{imgData}

where program_no = #{programNo}
